// Input Sanitization and Security
const sanitizeInput = (input) => {
  if (!input || typeof input !== 'string') return '';
  return input
    .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
    .replace(/[<>"']/g, '')
    .trim()
    .substring(0, 1000); // Limit length
};

// Rate limiting check
const userId = $json.sessionId || 'anonymous';
const now = new Date();
const rateLimitKey = `rate_limit_${userId}_${now.toISOString().split('T')[0]}`;

// Sanitize input
const sanitizedInput = sanitizeInput($json.chatInput);

if (!sanitizedInput) {
  throw new Error('Invalid input provided');
}

return {
  ...$json,
  chatInput: sanitizedInput,
  userId: userId,
  timestamp: now.toISOString(),
  rateLimitKey: rateLimitKey
};

// Configuration Management
const config = {
  businessHours: {
    start: 9,
    end: 17,
    timezone: 'Asia/Kolkata'
  },
  meetingConstraints: {
    minDuration: 0.5,
    maxDuration: 4,
    advanceBookingHours: 24,
    maxAdvanceBookingDays: 90
  },
  notifications: {
    reminderHours: [24, 2],
    adminTelegram: '1528112308'
  },
  rateLimits: {
    meetingsPerDay: 10,
    messagesPerHour: 50
  },
  workingDays: [1, 2, 3, 4, 5] // Monday to Friday
};

return {
  ...$json,
  config: config,
  timestamp: new Date().toISOString()
};

// Enhanced validation with business logic
const response = $json.output;
const config = $('Configuration Manager').first().json.config;
const missing = [];
const errors = [];

console.log("This is the config", config);

// Basic field validation
if (!response.name) missing.push('name');
if (!response.email) missing.push('email');
if (!response.date) missing.push('date and time');
if (!response.reason) missing.push('reason for meeting');
if (!response.duration) missing.push('duration of meeting');

// Email format validation
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (response.email && !emailRegex.test(response.email)) {
  errors.push('Invalid email format');
}

// Date and time validation
if (response.date) {
  const meetingDate = new Date(response.date);
  const now = new Date();
  const minAdvanceTime = new Date(now.getTime() + (config.meetingConstraints.advanceBookingHours * 60 * 60 * 1000));
  const maxAdvanceTime = new Date(now.getTime() + (config.meetingConstraints.maxAdvanceBookingDays * 24 * 60 * 60 * 1000));

  // Check if date is valid
  if (isNaN(meetingDate.getTime())) {
    errors.push('Invalid date format');
  } else {
    // Check if date is in the future
    if (meetingDate <= now) {
      errors.push('Meeting date must be in the future');
    }

    // Check advance booking constraints
    if (meetingDate < minAdvanceTime) {
      errors.push(`Meeting must be scheduled at least ${config.meetingConstraints.advanceBookingHours} hours in advance`);
    }

    if (meetingDate > maxAdvanceTime) {
      errors.push(`Meeting cannot be scheduled more than ${config.meetingConstraints.maxAdvanceBookingDays} days in advance`);
    }

    // Convert meeting date to IST timezone for business hours validation
    const istOffset = 5.5 * 60; // IST is UTC+5:30
    const meetingDateIST = new Date(meetingDate.getTime() + (istOffset * 60 * 1000));
    const hour = meetingDateIST.getUTCHours();

    console.log("Meeting date in IST:", meetingDateIST.toISOString());
    console.log("This is the hour in IST:", hour);

    if (hour < config.businessHours.start || hour >= config.businessHours.end) {
      errors.push(`Meeting must be scheduled during business hours (${config.businessHours.start}:00 - ${config.businessHours.end}:00 ${config.businessHours.timezone})`);
    }

    // Check working days (convert to IST for day calculation)
    const dayOfWeek = meetingDateIST.getUTCDay();
    console.log("Day of week in IST:", dayOfWeek);

    if (!config.workingDays.includes(dayOfWeek)) {
      const workingDayNames = config.workingDays.map(day => 
        ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][day]
      ).join(', ');
      errors.push(`Meeting must be scheduled on working days (${workingDayNames})`);
    }
  }
}

// Duration validation
if (response.duration) {
  const duration = parseFloat(response.duration);
  if (isNaN(duration)) {
    errors.push('Invalid duration format - must be a number');
  } else if (duration < config.meetingConstraints.minDuration || duration > config.meetingConstraints.maxDuration) {
    errors.push(`Meeting duration must be between ${config.meetingConstraints.minDuration} and ${config.meetingConstraints.maxDuration} hours`);
  }
}

console.log("These are the errors:", errors);

const isComplete = missing.length === 0;
const isValid = errors.length === 0;

return {
  ...response,
  missing: missing,
  errors: errors,
  isComplete: isComplete,
  isValid: isValid,
  validationStatus: isComplete && isValid ? 'VALID' : isComplete ? 'INVALID_DATA' : 'INCOMPLETE_DATA',
  meetingDate: response.date ? new Date(response.date) : null
};

