// Get all the results from Generate Analytics
const results = $input.all();

// Group results by period (we need to get period info from the split node)
const processedResults = [];

// Process results in chunks of status groups (every 3-4 items represent one period)
// Since we have 4 periods and varying status counts, we need to reconstruct this properly

// For now, let's aggregate all results into a comprehensive summary
const statusMetrics = {};
let totalMeetings = 0;
let totalDuration = 0;
const uniqueUsers = new Set();
const meetingTypes = new Set();

// Initialize default statuses
['PENDING', 'APPROVED', 'REJECTED', 'CANCELLED'].forEach(status => {
  statusMetrics[status] = {
    count: 0,
    avgDuration: 0,
    totalDuration: 0
  };
});

// Process all results
results.forEach(result => {
  const item = result.json;
  const status = item.status || item._id;

  // Aggregate by status
  if (!statusMetrics[status]) {
    statusMetrics[status] = { count: 0, avgDuration: 0, totalDuration: 0 };
  }

  statusMetrics[status].count += item.count || 0;
  statusMetrics[status].totalDuration += item.totalDuration || 0;
  totalMeetings += item.count || 0;
  totalDuration += item.totalDuration || 0;

  // Collect unique users and meeting types
  if (item.uniqueUsers) {
    item.uniqueUsers.forEach(user => uniqueUsers.add(user));
  }
  if (item.meetingTypes) {
    item.meetingTypes.forEach(type => meetingTypes.add(type));
  }
});

// Calculate average durations
Object.keys(statusMetrics).forEach(status => {
  if (statusMetrics[status].count > 0) {
    statusMetrics[status].avgDuration = Math.round((statusMetrics[status].totalDuration / statusMetrics[status].count) * 100) / 100;
  }
});

// Calculate overall metrics
const approvalRate = totalMeetings > 0 ? 
  Math.round((statusMetrics.APPROVED.count / totalMeetings) * 100) : 0;

const rejectionRate = totalMeetings > 0 ? 
  Math.round((statusMetrics.REJECTED.count / totalMeetings) * 100) : 0;

const avgMeetingDuration = totalMeetings > 0 ? 
  Math.round((totalDuration / totalMeetings) * 100) / 100 : 0;

return [{
  period: 'consolidated', // All periods combined for now
  timestamp: new Date().toISOString(),
  timezone: 'Asia/Kolkata',
  metrics: {
    summary: {
      totalMeetings: totalMeetings,
      totalDuration: totalDuration,
      avgMeetingDuration: avgMeetingDuration,
      uniqueUsers: uniqueUsers.size,
      meetingTypes: Array.from(meetingTypes),
      approvalRate: approvalRate,
      rejectionRate: rejectionRate
    },
    byStatus: statusMetrics,
    rawData: results.length // For debugging
  }
}];

