  meetingDate = new Date(response.meetingDate);
  const now = new Date();

  // Check if date is valid
  if (isNaN(meetingDate.getTime())) {
    errors.push('Invalid date format provided');
  } else {
    // Check if meeting is in the past
    if (meetingDate <= now) {
      errors.push('Cannot cancel meetings that have already occurred');
    }

    // Convert meeting date to IST for business hours validation (if needed)
    const istOffset = 5.5 * 60; // IST is UTC+5:30
    const meetingDateIST = new Date(meetingDate.getTime() + (istOffset * 60 * 1000));
    const dayOfWeek = meetingDateIST.getUTCDay();

    console.log("Meeting date in IST:", meetingDateIST.toISOString());
    console.log("Day of week:", dayOfWeek);
  }
}

// Additional business logic validation
const businessValidation = {
  hasValidEmail: response.email && emailRegex.test(response.email),
  hasValidDate: meetingDate && !isNaN(meetingDate.getTime()),
  isInFuture: meetingDate && meetingDate > new Date(),
};

console.log("Business validation results:", businessValidation);

// Determine validation status
const isComplete = missing.length === 0;
const isValid = errors.length === 0;
const validationStatus = isComplete && isValid ? 'VALID' : 
                        isComplete ? 'INVALID_DATA' : 'INCOMPLETE_DATA';

// Prepare search criteria for database lookup
const searchCriteria = {
  email: response.email,
  date: response.meetingDate,
  dateRange: null
};

// If exact date matching fails, we might want to search by date range
if (meetingDate && isValid) {
  const startOfDay = new Date(meetingDate);
  startOfDay.setHours(0, 0, 0, 0);
  const endOfDay = new Date(meetingDate);
  endOfDay.setHours(23, 59, 59, 999);

  searchCriteria.dateRange = {
    start: startOfDay.toISOString(),
    end: endOfDay.toISOString()
  };
}

// Generate user-friendly error messages
const friendlyMessages = {
  missingInfo: missing.length > 0 ? `I need the following information to cancel your meeting: ${missing.join(', ')}.` : null,
  validationErrors: errors.length > 0 ? `There are some issues with your cancellation request: ${errors.join(' ')}` : null,
  warnings: warnings.length > 0 ? warnings.join(' ') : null
};

console.log("Validation completed with status:", validationStatus);

return {
  // Original extracted data
  email: response.email,
  meetingDate: response.meetingDate,
  reason: response.reason || 'No reason provided',

  // Validation results
  missing: missing,
  errors: errors,
  warnings: warnings,
  isComplete: isComplete,
  isValid: isValid,
  validationStatus: validationStatus,

  // Processed data
