const meetings = $input.all();
let message = `üîç I found ${meetings.length} meetings for your email address. Please specify which one you want to cancel:\n\n`;

meetings.forEach((item, idx) => {
  const m = item.json;
  const date = new Date(m.date).toLocaleString('en-IN', { 
    timeZone: 'Asia/Kolkata',
    dateStyle: 'full',
    timeStyle: 'short'
  });
  message += `**${idx + 1}. Meeting on ${date}**\n- Purpose: ${m.reason}\n- Duration: ${m.duration} hour${m.duration === 1 ? '' : 's'}\n- Status: ${m.status}\n- Type: ${m.meetingType}\n\n`;
});

message += `**To cancel a specific meeting, please say:**\n‚Ä¢ "Cancel meeting number 1" or\n‚Ä¢ "Cancel the meeting on [specific date/time]" or  \n‚Ä¢ "Cancel my [meeting purpose] meeting"\n\nWhich meeting would you like to cancel? ü§î`;

// Return with proper pairedItem structure
return meetings.map((item, index) => ({
  json: { 
    message: message,
    // Include original data to maintain context
    ...item.json
  },
  pairedItem: item.pairedItem
}));

